!function(){function init(){if(document.getElementById("button").getAttribute("data-keyboard")){document.querySelector("[data-exit-button]").addEventListener("click",exit),console.log("EXIT button is ACTIVE!");var btnColor=document.getElementById("button").getAttribute("data-color"),btnBackground=document.getElementById("button").getAttribute("data-background"),btnBorder=document.getElementById("button").getAttribute("data-border");"false"===status?(document.querySelector("[data-exit-button]").style.color="white",document.querySelector("[data-exit-button]").style.background="darkred",document.querySelector("[data-exit-button]").style.border="3px solid red"):(document.querySelector("[data-exit-button]").style.color=btnColor,document.querySelector("[data-exit-button]").style.background=btnBackground,document.querySelector("[data-exit-button]").style.border=btnBorder)}else console.log("EXIT button is NOT ACTIVE!")}function exit(){window.location.replace(document.getElementById("button").getAttribute("data-exit-url"))}window.onload=init(),document.addEventListener("keydown",function(e){var nodeIdentifier=e.target.nodeName,valueFromKeyboard=e.which,userKeyCode=document.getElementById("button").getAttribute("data-key");document.getElementById("button").getAttribute("data-keyboard")&&valueFromKeyboard==userKeyCode&&"BODY"==nodeIdentifier&&(console.log("Redirecting..."),exit())})}();